      "school": "conjuration",
      "subschool": "creation",
      "descriptor": "",
      "spell_level": "bard 1, sorcerer/wizard 1, summoner/unchained summoner 1, witch 1, magus 1, occultist 1, psychic 1, mesmerist 1, spiritualist 1, medium 1",
      "casting_time": "1 standard action",
