      "name": "Whispering Wind",
      "school": "transmutation",
      "subschool": "",
      "descriptor": "air",
      "spell_level": "bard 2, sorcerer/wizard 2, inquisitor 2, psychic 2, mesmerist 2, medium 2",
      "casting_time": "1 standard action",
