      "name": "Daylight",
      "school": "evocation",
      "subschool": "",
      "descriptor": "light",
      "spell_level": "bard 3, cleric/oracle 3, druid 3, paladin 3, sorcerer/wizard 3, inquisitor 3, magus 3, shaman 3, occultist 3",
      "casting_time": "1 standard action",
      "components": "V, S",
      "costly_components": 0,
      "range": "touch",
      "area": "",
