      "descriptor": "",
      "spell_level": "bard 3, sorcerer/wizard 3, occultist 3, mesmerist 3",
      "casting_time": "10 minutes",
      "components": "V, S, M (powdered herring scales and a vial of will-o'-wisp essence)",
      "costly_components": 0,
      "range": "touch",
