      "descriptor": "",
      "spell_level": "bard 4, sorcerer/wizard 4, summoner 3, witch 4, occultist 4, psychic 4, mesmerist 4, spiritualist 4, unchained summoner 4, medium 3",
      "casting_time": "1 standard action",
      "components": "V, S, M (fur from a bloodhound)",
      "costly_components": 0,
      "range": "long (400 ft. + 40 ft./level)",
