      "subschool": "compulsion",
      "descriptor": "mind-affecting, emotion",
      "spell_level": "bard 2, cleric/oracle 2, inquisitor 2, shaman 2, occultist 2, psychic 2, mesmerist 2",
      "casting_time": "1 standard action",
      "components": "V, S, DF",
      "costly_components": 0,
      "range": "medium (100 ft. + 10 ft./level)",
      "area": "creatures in a 20-ft.-radius spread",
