      "name": "Mark of Justice",
      "school": "necromancy",
      "subschool": "",
      "descriptor": "curse",
      "spell_level": "cleric/oracle 5, paladin 4, witch 5, inquisitor 5",
      "casting_time": "10 minutes",
      "components": "V, S, DF",
      "costly_components": 0,
      "range": "touch",
