      "name": "Plane Shift",
      "school": "conjuration",
      "subschool": "teleportation",
      "descriptor": "",
      "spell_level": "cleric/oracle 5, sorcerer/wizard 7, summoner 5, witch 7, shaman 7, psychic 5, spiritualist 5, unchained summoner 6, medium 4",
      "casting_time": "1 standard action",
      "components": "V, S, F (a forked metal rod attuned to the plane of travel)",
      "costly_components": 0,
      "range": "touch",
