      "name": "Speak with Dead",
      "school": "necromancy",
      "subschool": "",
      "descriptor": "language-dependent",
      "spell_level": "cleric/oracle 3, witch 3, inquisitor 3, shaman 3, psychic 3, spiritualist 3, medium 2",
