    "descriptor": "",
    "spell_level": "bard 6, cleric/oracle 6, druid 6, witch 6, inquisitor 6, shaman 6, occultist 6, psychic 6, spiritualist 6",
    "casting_time": "3 rounds",
    "components": "V, S, F (a set of divination counters)",
