    "spell_level": "bard 4, cleric/oracle 4, druid 4, ranger 4, alchemist 4, inquisitor 4, alchemist 4, occultist 4, psychic 4, mesmerist 4, spiritualist 4, medium 4",
    "casting_time": "1 standard action",
    "components": "V, S, M (a leather strip bound to the target), DF",
    "costly_components": 0,
    "range": "personal or touch",
