      "descriptor": "",
      "spell_level": "bard 4, cleric/oracle 4, druid 3, paladin 4, ranger 3, alchemist 4, witch 4, inquisitor 4, shaman 4, spiritualist 4",
      "casting_time": "1 standard action",
      "components": "V, S, M/DF (charcoal)",
      "costly_components": 0,
      "range": "touch",
